function add_custom_fields() {
  register_graphql_field('user', 'twitter', [
    'type' => 'String',
    'args' => [],
    'resolve' => function ($source) {
		$fields = get_user_meta($source->userId, 'twitter', true);
		if ( isset( $fields ) ) {
      		return $fields;
   		}
    	return null;
	}
  ]);
}

add_action( 'graphql_register_types', 'add_custom_fields');
